import { xStart, yStart, wrapText, pageSize, addHeader } from './pdfBase';
import { obsColWidth } from './pdfTables';
import { rgb } from 'pdf-lib';

// Fun√ß√£o para calcular a altura exata da tabela de observa√ß√µes
export function calculateObsTableHeight(dataObs, font) {
  const obsRows = 5; // Sempre 5 se√ß√µes
  const safeDataObs = Array.from({ length: obsRows }, (_, i) =>
    Array.isArray(dataObs) && Array.isArray(dataObs[i]) ? dataObs[i] : [""]
  );

  const fontSize = 8;
  const maxWidth = obsColWidth[0] - 8;
  const lineHeight = fontSize + 2;
  const headerHeight = 25; // Altura dos headers
  const minContentHeight = 30; // Altura m√≠nima do conte√∫do

  // Calcula altura de cada se√ß√£o (header + conte√∫do)
  const sectionHeights = safeDataObs.map((row) => {
    const text = row[0] || '';
    const lines = wrapText(text, font, fontSize, maxWidth);
    const contentHeight = Math.max(minContentHeight, lines.length * lineHeight + 16);
    return headerHeight + contentHeight;
  });

  return sectionHeights.reduce((a, b) => a + b, 0);
}

// Fun√ß√£o para desenhar tabela de observa√ß√µes com headers cinza e continua√ß√£o entre p√°ginas
export async function drawObsTableWithHeaders(page, font, dataObs, imageBytes = null, pathFilename = '') {
  const obsRows = 5; // Sempre 5 se√ß√µes
  const headers = [
    "1. Objetivos:",
    "2. Campo de Aplica√ß√£o:", 
    "3. Defini√ß√µes:",
    "4. Abreviaturas:",
    "5. Observa√ß√µes:"
  ];
  
  // Garante que temos exatamente 5 se√ß√µes na ordem correta
  const safeDataObs = Array.from({ length: obsRows }, (_, i) => {
    if (Array.isArray(dataObs) && Array.isArray(dataObs[i])) {
      return dataObs[i];
    } else if (Array.isArray(dataObs) && dataObs[i] !== undefined) {
      return [dataObs[i]]; // Converte string em array se necess√°rio
    } else {
      return [""]; // Se√ß√£o vazia
    }
  });

  console.log('üîç DEBUG - Se√ß√µes processadas para PDF:', safeDataObs.map((section, i) => 
    `${headers[i]} -> "${section[0] ? section[0].substring(0, 50) : 'vazio'}..."`
  ));

  const fontSize = 8;
  const maxWidth = obsColWidth[0] - 8;
  const lineHeight = fontSize + 2;
  const headerHeight = 25;
  const minContentHeight = 30;
  const pageBottomMargin = 50; // Margem inferior da p√°gina
  const maxYPosition = pageBottomMargin; // Posi√ß√£o Y m√≠nima na p√°gina

  let currentPage = page;
  let yPos = yStart;
  let allPages = [page]; // Array para armazenar todas as p√°ginas criadas
  
  // Fun√ß√£o auxiliar para criar nova p√°gina
  const createNewPage = async () => {
    const newPage = currentPage.doc.addPage(pageSize);
    await addHeader(newPage, font, '', imageBytes, pathFilename);
    allPages.push(newPage);
    currentPage = newPage;
    yPos = yStart; // Reset da posi√ß√£o Y para o topo da nova p√°gina
    return newPage;
  };

  // Fun√ß√£o auxiliar para verificar se h√° espa√ßo suficiente na p√°gina atual
  const hasSpaceForContent = (requiredHeight) => {
    return yPos - requiredHeight >= maxYPosition;
  };

  // Fun√ß√£o auxiliar para dividir texto em chunks que cabem na p√°gina
  const splitTextForPages = (lines, availableHeight) => {
    const linesPerPage = Math.floor((availableHeight - 16) / lineHeight); // -16 para padding
    const chunks = [];
    
    for (let i = 0; i < lines.length; i += linesPerPage) {
      chunks.push(lines.slice(i, i + linesPerPage));
    }
    
    return chunks;
  };
  
  // Desenha cada se√ß√£o
  for (let i = 0; i < obsRows; i++) {
    const text = safeDataObs[i][0] || '';
    const lines = wrapText(text, font, fontSize, maxWidth);
    const contentHeight = Math.max(minContentHeight, lines.length * lineHeight + 16);
    const sectionHeight = headerHeight + contentHeight;
    
    // Verifica se a se√ß√£o completa cabe na p√°gina atual
    if (!hasSpaceForContent(sectionHeight)) {
      // Se nem o header cabe, cria nova p√°gina
      if (!hasSpaceForContent(headerHeight)) {
        await createNewPage();
      }
      
      // Se ainda n√£o cabe ap√≥s nova p√°gina, divide o conte√∫do
      if (!hasSpaceForContent(sectionHeight)) {
        // Desenha header na p√°gina atual
        currentPage.drawRectangle({
          x: xStart,
          y: yPos - headerHeight,
          width: obsColWidth[0],
          height: headerHeight,
          color: rgb(0.7, 0.7, 0.7),
          borderColor: rgb(0, 0, 0),
          borderWidth: 1,
        });
        
        currentPage.drawText(headers[i], {
          x: xStart + 8,
          y: yPos - 15,
          size: 8,
          font,
          color: rgb(0, 0, 0),
        });
        
        yPos -= headerHeight;
        
        // Calcula quanto espa√ßo dispon√≠vel temos para conte√∫do na p√°gina atual
        const availableSpaceCurrentPage = yPos - maxYPosition;
        
        // Divide o texto em chunks que cabem nas p√°ginas
        const textChunks = splitTextForPages(lines, availableSpaceCurrentPage);
        
        // Desenha primeiro chunk na p√°gina atual
        if (textChunks.length > 0) {
          const firstChunkHeight = Math.max(minContentHeight, textChunks[0].length * lineHeight + 16);
          
          currentPage.drawRectangle({
            x: xStart,
            y: yPos - firstChunkHeight,
            width: obsColWidth[0],
            height: firstChunkHeight,
            borderColor: rgb(0, 0, 0),
            borderWidth: 1,
          });
          
          let textY = yPos - 12;
          for (let j = 0; j < textChunks[0].length; j++) {
            currentPage.drawText(textChunks[0][j], {
              x: xStart + 4,
              y: textY - j * lineHeight,
              size: fontSize,
              font,
              color: rgb(0, 0, 0),
              maxWidth: maxWidth,
            });
          }
          
          yPos -= firstChunkHeight;
        }
        
        // Desenha chunks restantes em novas p√°ginas
        for (let chunkIndex = 1; chunkIndex < textChunks.length; chunkIndex++) {
          await createNewPage();
          
          const chunk = textChunks[chunkIndex];
          const chunkHeight = Math.max(minContentHeight, chunk.length * lineHeight + 16);
          
          // Desenha header de continua√ß√£o (mais claro)
          currentPage.drawRectangle({
            x: xStart,
            y: yPos - headerHeight,
            width: obsColWidth[0],
            height: headerHeight,
            color: rgb(0.85, 0.85, 0.85), // Cor mais clara para indicar continua√ß√£o
            borderColor: rgb(0, 0, 0),
            borderWidth: 1,
          });
          
          currentPage.drawText(`${headers[i]} (continua√ß√£o)`, {
            x: xStart + 8,
            y: yPos - 15,
            size: 8,
            font,
            color: rgb(0, 0, 0),
          });
          
          yPos -= headerHeight;
          
          // Desenha conte√∫do da continua√ß√£o
          currentPage.drawRectangle({
            x: xStart,
            y: yPos - chunkHeight,
            width: obsColWidth[0],
            height: chunkHeight,
            borderColor: rgb(0, 0, 0),
            borderWidth: 1,
          });
          
          let textY = yPos - 12;
          for (let j = 0; j < chunk.length; j++) {
            currentPage.drawText(chunk[j], {
              x: xStart + 4,
              y: textY - j * lineHeight,
              size: fontSize,
              font,
              color: rgb(0, 0, 0),
              maxWidth: maxWidth,
            });
          }
          
          yPos -= chunkHeight;
        }
        
      } else {
        // A se√ß√£o cabe inteira na nova p√°gina
        await createNewPage();
        
        // Desenha header
        currentPage.drawRectangle({
          x: xStart,
          y: yPos - headerHeight,
          width: obsColWidth[0],
          height: headerHeight,
          color: rgb(0.7, 0.7, 0.7),
          borderColor: rgb(0, 0, 0),
          borderWidth: 1,
        });
        
        currentPage.drawText(headers[i], {
          x: xStart + 8,
          y: yPos - 15,
          size: 8,
          font,
          color: rgb(0, 0, 0),
        });
        
        // Desenha √°rea de conte√∫do
        currentPage.drawRectangle({
          x: xStart,
          y: yPos - sectionHeight,
          width: obsColWidth[0],
          height: contentHeight,
          borderColor: rgb(0, 0, 0),
          borderWidth: 1,
        });
        
        // Desenha conte√∫do
        let textY = yPos - headerHeight - 12;
        for (let j = 0; j < lines.length; j++) {
          currentPage.drawText(lines[j], {
            x: xStart + 4,
            y: textY - j * lineHeight,
            size: fontSize,
            font,
            color: rgb(0, 0, 0),
            maxWidth: maxWidth,
          });
        }
        
        yPos -= sectionHeight;
      }
    } else {
      // A se√ß√£o cabe inteira na p√°gina atual
      
      // Desenha header cinza
      currentPage.drawRectangle({
        x: xStart,
        y: yPos - headerHeight,
        width: obsColWidth[0],
        height: headerHeight,
        color: rgb(0.7, 0.7, 0.7),
        borderColor: rgb(0, 0, 0),
        borderWidth: 1,
      });
      
      // Texto do header
      currentPage.drawText(headers[i], {
        x: xStart + 8,
        y: yPos - 15,
        size: 8,
        font,
        color: rgb(0, 0, 0),
      });
      
      // Desenha √°rea de conte√∫do
      currentPage.drawRectangle({
        x: xStart,
        y: yPos - sectionHeight,
        width: obsColWidth[0],
        height: contentHeight,
        borderColor: rgb(0, 0, 0),
        borderWidth: 1,
      });
      
      // Desenha conte√∫do
      let textY = yPos - headerHeight - 12;
      for (let j = 0; j < lines.length; j++) {
        currentPage.drawText(lines[j], {
          x: xStart + 4,
          y: textY - j * lineHeight,
          size: fontSize,
          font,
          color: rgb(0, 0, 0),
          maxWidth: maxWidth,
        });
      }
      
      yPos -= sectionHeight;
    }
  }
  
  // Retorna informa√ß√µes sobre as p√°ginas criadas
  return {
    totalHeight: 0, // N√£o aplic√°vel com m√∫ltiplas p√°ginas
    pages: allPages,
    currentPage: currentPage,
    currentYPosition: yPos
  };
}
